#!/bin/bash# Wrapper to run the coloring pipeline with optional venv# -------------------------------------------------------------# Usage:# Place this .sh file in the same folder as process_coloring_pipeline.py.# Make it executable in Terminal (one time only):# chmod +x run_pipeline.sh# # Double-click it or run from Terminal:# ./run_pipeline.sh# -------------------------------------------------------------# Change directory to the folder containing the scriptcd "$(dirname "$0")"# Check for Python 3if ! command -v python3 &> /dev/null; then    echo "Error: Python 3 is not installed or not in your PATH."    echo "Press Enter to exit..."    read    exit 1fi# Activate virtual environment if it existsif [ -d "venv" ]; then    echo "Activating virtual environment..."    source venv/bin/activatefi# Run the Python scriptpython3 process_coloring_pipeline_adv.pyechoecho "Processing complete. Press Enter to exit..."read